import React, { useState } from 'react';

const editcurreny = (props) =>{
    const [ currentcurr , setCurrentcurr ] = useState(props.currency);
    return(
        <>
            <input type='text' required='required' onChange={(event)=> setCurrentcurr(event.target.value)}>

            </input>
            {curr}
            <button
				type='button'
				class='btn btn-primary'
				onClick={() => props.handleSaveClick()}
			>
				Save
			</button>

        </>
    );

};

export default editcurreny;
