import React, { useContext, useState } from 'react';
import viewcurrency  from './viewcurrency';
import editcurreny  from './editcurrency';
import { AppContext } from '../context/AppContext';


const changecurr = (props) => {
    const { currency, dispatch } = useContext(AppContext);
    const [ isEditing, setIsEditing] = useState(false);

	const handleEditClick = () => {
		setIsEditing(true);
	};
    const handleSaveClick = () => {
		dispatch({
			type: 'CHG_CURRENCY',
			payload: currency,
		});
		setIsEditing(false);
	};

    return(
        <div class='alert alert-secondary p-3 d-flex align-items-center justify-content-between'>
			{isEditing ? (
				<editcurreny handleSaveClick={handleSaveClick} currency={currency} />
			) : (

				<viewcurrency handleEditClick={handleEditClick} currency={currency} />
			)}
		</div>
    );
};

export default changecurr;
