import React, { useState } from 'react';

const editcurreny = (props) =>{
    const [ currentcurr , setCurrentcurr ] = useState(props.currency);
    return(
        <>
            <select onChange={(event)=> setCurrentcurr(event.target.value)}>
                <option defaultValue>{currentcurr}</option>
                <option>€ Euro</option>
                <option> ₨ Ruppee </option>
            </select>
            <button
				type='button'
				class='btn btn-primary'
				onClick={() => props.handleSaveClick()}
			>
				Save
			</button>

        </>
    );

};

export default editcurreny;
